package day03;

public class 변수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//변수 : 하나의 값을 저장하기 위한 메모리 공간( 일정량의 바이트를 가진다 )
		//자료형은 크기(몇바이트) + 해석의 정보를 가지고 있다 ( 정수형, 실수형, 문자 )
	
		//정수형숫자
		int a=10;
		
		//실수형숫자
		double b = 253.23;
		
		//문자, 하나의 문자 : ' '감싼다
		char c = 'A';	// 한 문자 저장
		
		//논리형
		boolean flag = true;  // 참 거짓 저장하는 변수
	
		
		//문자열
		//문자집합(문자배열)
		
		//자바는 문자열을 위해서 String 클래스를 제공한다
		
		String name="hello java";
		
		
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(flag);
		System.out.println(name);

		
		//변수이름 영문 소문자, 의미있게 작성
	    //카멜케이스 작명법, 언더스코아(스네이크법)
	    //mathScore, math_score

		
	}

}
